import React, { useState, useEffect } from 'react';
import { useHistory } from "react-router-dom";

import Layout from '../../components/Layout';

import HomeNews from '../../components/HomeNews';
import HomePreach from '../../components/HomePreach';
import HomeFairs from '../../components/HomeFairs';
import HomeBanner from './components/HomeBanner';
import HomeZhaopin from '../../components/HomeZhaopin';
import HomeZhiwei from '../../components/HomeZhiwei';
import HomePass from '../../components/HomePass';
import CalendarWrap from '../../components/CalendarWrap';
import HomeShouxu from '../../components/HomeShouxu';
import HomePartners from '../../components/HomePartners';
import Ncss from './components/Ncss';

import {
  getIndexNews,
  getIndexFairs,
  getIndexJobs,
  getIndexNotices,
  getIndexPreaches,
  getIndexAllKindInfo,
} from '../../../api/index';


import {
  tabDataIndexFairsConfig,
  tabDataIndexNoticesConfig,
  unitZhaopinTabData,
} from '../../config';

import './index.less';

export default function Home() {
  const history = useHistory();

  const [indexNewsList, setIndexNewsList] = useState([]);

  // 第一个tab
  const [tab0List0, setTab0List0] = useState(null);
  const [tab0List1, setTab0List1] = useState(null);
  const [tab0List2, setTab0List2] = useState(null);

  // 第二个tab
  const [tab1List0, setTab1List0] = useState(null);
  const [tab1List1, setTab1List1] = useState(null);
  const [tab1List2, setTab1List2] = useState(null);
  const [tab1List3, setTab1List3] = useState(null);
  const [tab1List4, setTab1List4] = useState(null);
  const [tab1List5, setTab1List5] = useState(null);
  const [tab1List6, setTab1List6] = useState(null);

  // 第三个tab
  const [tab2List0, setTab2List0] = useState(null);
  const [tab2List1, setTab2List1] = useState(null);
  const [tab2List2, setTab2List2] = useState(null);
  const [tab2List3, setTab2List3] = useState(null);
  const [tab2List4, setTab2List4] = useState(null);
  const [tab2List5, setTab2List5] = useState(null);
  const [tab2List6, setTab2List6] = useState(null);

  // 获取数据
  useEffect(() => {
    // 新闻
    const newsParams = {
      size: 6
    };
    getIndexNews(newsParams).then((res) => {
      console.log('newsParams');
      console.log(res);
      setIndexNewsList(res.data?.infos || []);
    });

    initData();

  }, [history]);

  const initData = () => {
    // 宣讲会
    const setTab0List0Params = {
      size: 6,
      mode: '',
    };
    getIndexPreaches(setTab0List0Params).then((res) => {
      if (res.data && res.data.preaches) {
        setTab0List0(res.data.preaches);
      }
    });

    // 双选会
    const setTab0List1params = {
      size: 6,
      mode: '',
    };
    getIndexFairs(setTab0List1params).then((res) => {
      if (res.data && res.data.fairs) {
        setTab0List1(res.data.fairs);
      }
    });

    // 重点宣讲会
    const setTab0List2Params = {
      category: 'JIC015',
      page: 1,
      pageSize: 6,
    };
    getIndexAllKindInfo(setTab0List2Params).then((res) => {
      console.log('getIndexAllKindInfo');
      console.log(res);
      if (res.data && res.data.fairs) {
        setTab0List2(res.data.fairs);
      }
    });

    // 在线招聘
    const setTab1List0Params = {
      size: 6,
      normal: true,
    };
    getIndexJobs(setTab1List0Params).then((res) => {
      if (res.data && res.data.jobs) {
        setTab1List0(res.data.jobs);
      }
    });

    // 高校教师
    const setTab1List1Params = {
      category: 'JIC001',
      page: 1,
      pageSize: 20,
    };
    getIndexAllKindInfo(setTab1List1Params).then((res) => {
      if (res.data && res.data.jobs) {
        setTab1List1(res.data.jobs);
      }
    });
  }

  return (
    <div className="page home">
      <Layout>
        {/* banner */}

        <div className="normal-row">
          {/* 新闻公告 */}
          <HomeNews list={indexNewsList}/>

          <div className="home-rili gap-top-l normal-col">
            <div className="home-title">日历</div>
            <CalendarWrap />
          </div>
        </div>

        <div className="normal-row">
          <div className="home-left">
            {/* 双选会 */}
            <HomeFairs
              lists={[tab0List0, tab0List1, tab0List2]}
              data={tabDataIndexFairsConfig}
            />

            {/* 招聘 */}
            {/* {
              (tabDataIndexFairs0 || tabDataIndexFairs1)
              && (
                <HomeFairs list0={tabDataIndexFairs0} list1={tabDataIndexFairs1} data={tabDataIndexNoticesConfig} />
              )
            } */}

            {/* 事业单位 */}
            {/* {
              (tabDataIndexFairs0 || tabDataIndexFairs1)
              && (
                <HomeFairs list0={tabDataIndexFairs0} list1={tabDataIndexFairs1} data={unitZhaopinTabData} />
              )
            } */}
          </div>

          <div className="home-right gap-top-m normal-col">
            <Ncss />
            <HomePartners />
          </div>
        </div>
      </Layout>
    </div>
  );
}
