import React, { useState, Fragment } from 'react';

import './index.less';

const hot = require('../../assets/images/news-top.png');
const nothot = require('../../assets/images/news-normal.png');

export default function ItemNews({
  data, hasBefore, hasTime, beforeType, onClick, pathName
}) {
  const handleClick = () => {
    if (onClick) {
      onClick(data);
    } else {
      window.open(`/${pathName}?id=${data.infoId}&secRNav=20`);
    }
  }
  return (
    <div
      onClick={handleClick}
      key={data.title}
      className={`news-item news-item-like hover-item ${data.listWeight > 0 ? 'news-item-ishot' : ''} news-item-before${beforeType}`}
    >
      <div>
        {
          <div className="ishot">
            {
              data.listWeight > 0
              ? <img alt="" src={hot} />
              : <img alt="" src={nothot} />
            }
          </div>
        }

        <div className="title c-line-clamp1">
          {data.name}
        </div>

        {/* 地址 */}
        <span className="tag">
          {
            data.mode === 'OFFLINE'
            ? data.place.name
            : '线上宣讲'
          }
        </span>

      </div>

      {
        hasTime && (
          <div className="time">
            {data.preachDate}
          </div>
        )
      }
    </div>
  );
}
